import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'demo';

  parentMessage: string ="Hi...";

  childMessage: string = "";

  GetMessage(res : any)
  {
    this.childMessage =res;
  }

}
